import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import logo1 from '../assets/logo1.jpg'
import trainService from '../services/train.service';

const Header = () => {


  const [train,setTrain]=useState([]);
  const [train_name,setTrainName]=useState([]);
  const search=(e)=>{
    e.preventDefault();

    const val = {
      train_name

    };

    trainService
      .getByTrainName(val)
      .then((response) => {
        console.log("Feedback added successfully", response.data);
        localStorage.setItem("jwt", response.data.jwt);
        localStorage.setItem("train_", val.username);

         window.location.href="/profile"
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
    localStorage.setItem("train_name", val.train_name);
    window.location.href="/TrainDetails"
  }
  // const getByTrainName=(id)=> {
  //   console.log(id);
  //   trainService.getByTrainName(id).then((response)=> {
  //     console.log("All Data",response.data);
  //     setTrain(response.data);
  //   }).catch((error)=> {
  //     console.log("Something went wrong",error);
  //   });
  // }

  // useEffect(()=> {
  //   getByName();
  // },[]);

  return (
    <header className='header'>

      <div>
      <img
          src={logo1}
          alt=""
          style={{
             height: "26px",
             width: "66px",
             paddingRight: "20px",


          }}
        />
      </div>
      {/* <div>
        <Link className='links' to='/'>
          Home
        </Link>
      </div> */}

      <nav className='navbar' style={{height:"40px"}}>
        <ul>
        <Link className='links' to='/'>
          Home
        </Link>
          <Link className='links' to='/login'>
            Login
          </Link>
          <Link className='links' to='/signup'>
            Sign Up
          </Link>
          <Link className='links' to='/Contact'>
            Contact Us
          </Link>
          <Link className='links' to='/Train'>
           All trains
          </Link>
        </ul>
      </nav>
      <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              value={train_name}
              onChange={(e) => setTrainName(e.target.value)}
              className="me-2"
              aria-label="Search"
              style={{marginLeft: "480px",height:"30px",width:"290px"}}
            />
            {/* <Link className="btn btn-sucess" to="/TrainDetails" id="search" variant="secondary" style={{marginRight: "30px", color: "white"}} 
            onClick={(e) => {
              search(e);
            }}>
              Search
            </Link> */}
            <button
                    class="btn btn-primary homecontactbtn1"
                    onClick={(e) => {
                      search(e);
                    }}
                  >
                    Search
                  </button>
      </Form>
    </header>
  )
}

export default Header
