import React from "react";
import { useState, } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import userservice from "../services/user.service";

const SignUp = () => {
  const [username, setUsername] = useState("");
  const [emailId, setEmailId] = useState("");
  const [password, setPassword] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [ph_no, setPh_no] = useState("");
  const [roles, setRoles] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const createUser = (o) => {
    o.preventDefault();
    const u = { username, emailId, password, age, gender, ph_no, roles, id };

    userservice
      .create(u)
      .then((response) => {
        console.log("User added successfully", response.data);
        navigate("/createUser");
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };

  useEffect(() => {
    if (id) {
      userservice
        .get(id)
        .then((u) => {
          setUsername(u.data.username);
          setEmailId(u.data.emailId);
          setPassword(u.data.password);
          setGender(u.data.gender);
          setAge(u.data.age);
          setPh_no(u.data.ph_no);
          setRoles(u.data.roles);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <>
      <section className="showcase login">
        <div className="showcase-overlay">
          <form className="form-control">
            <input
              type="text"
              name="id"
              id="id"
              placeholder="UserId"
              required
            />
            <input
              type="text"
              name="username"
              id="username"
              placeholder="Username"
              required
            />
            <input
              type="email"
              name="email"
              id="email"
              placeholder="Your email address"
              required
            />
            <input
              type="password"
              name="password"
              id="password"
              placeholder="Choose your password"
            />
            <input
              type="text"
              name="age"
              id="age"
              placeholder="Enter your age"
            />
            <input
              type="number"
              name="ph_no"
              id="ph_no"
              placeholder="Enter Phone number"
              required
            />

            <input
              type="text"
              name="roles"
              id="roles"
              placeholder="Enter your role(User/Admin)"
              required
            />

            <button onClick={(o) => createUser(o)} className="btn btn-success">
              Create Account
            </button>
          </form>
        </div>
      </section>
    </>
  );
};

export default SignUp;
