import React from "react";
import { useState,useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import userservice from "../services/user.service";

const SignUp  = () => {
  const [username, setName] = useState("");
  const [emailId, setEmail] = useState("");

  const [password, setPass] = useState("");
  const [age, setAge] = useState("");

  const [gender, setGender] = useState("");
  const [ph_no, setPhone] = useState("");
  const [roles, setRoles] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const saveUser = (e) => {
    e.preventDefault();

    const user = { username, emailId, password, age, gender, ph_no, roles, id };

    if (id) {
      //update
      userservice
        .update(user)
        .then((response) => {
          console.log("user data updated successfully", response.data);
          navigate("/user");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      userservice
        .create(user)
        .then((response) => {
          console.log("user added successfully", response.data);
         navigate("/registraion");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (id) {
      userservice
        .get(id)
        .then((user) => {
          setName(user.data.username);
          setEmail(user.data.emailId);
          setPass(user.data.password);
          setAge(user.data.age);
          setGender(user.data.gender);
          setPhone(user.data.ph_no);
          setRoles(user.data.roles);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <>
      <section className="showcase login">
        <div className="showcase-overlay">
          <form className="form-control">
            <input
              type="text"
              name="username"
              id="username"
              placeholder="username"
              required
              value={username}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              type="text"
              name="emailId"
              id="emailId"
              placeholder="Your enailId "
              required
              value={emailId}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              name="password"
              id="password"
              placeholder="Your password"
              required
              value={password}
              onChange={(e) => setPass(e.target.value)}
            />
            <input
              type="gender"
              name="gender"
              id="gender"
              placeholder="Choose your gender"
              required
              value={gender}
              onChange={(e) => setGender(e.target.value)}
            />
            <input
              type="text"
              name="age"
              id="age"
              placeholder="Enter your age"
              required
              value={age}
              onChange={(e) => setAge(e.target.value)}
            />
            <input
              type="number"
              name="ph_no"
              id="ph_no"
              placeholder="Enter Phone number"
              required
              value={ph_no}
              onChange={(e) => setPh_no(e.target.value)}
            />

            <input
              type="text"
              name="roles"
              id="roles"
              placeholder="Enter your role(User/Admin)"
              required
              value={roles}
              onChange={(e) => setRoles(e.target.value)}
            />
            <div><button onClick={(o) => createUser(o)} className="btn btn-success">
              Create Account
            </button></div>





          </form>
        </div>
      </section>
    </>
  );
};

export default SignUp;
