import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



import bootstrap from "bootstrap/dist/css/bootstrap.min.css";
import Modal from "react-bootstrap/Modal";
import AuthService from "../services/auth-service";
let p = localStorage.getItem("username");
let t = localStorage.getItem("train_name");
let e = localStorage.getItem("email");
let tn = localStorage.getItem("train_no");
let fn = localStorage.getItem("fname");
let ln = localStorage.getItem("lname");
let age = localStorage.getItem("age");
// let bookingId = localStorage.getItem("bookingId");
let start_from = localStorage.getItem("start_from");
let destination = localStorage.getItem("destination");
let coach_no = localStorage.getItem("coach_no");
let seat_no = localStorage.getItem("seat_no");

const Fullticket = () => {
  const [user, setFaculty] = useState([]);



  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);


  const init = () => {
    AuthService.getUser(p)
      .then((response) => {
        console.log("Printing faculty data", response.data);
        setFaculty(response.data);
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };
  useEffect(() => {
    init();
  }, []);

  return (
    <div className="grid-container1">
      <div className="item2" style={{ marginLeft: "28rem", width: "35 rem" }}>
        <p> Name:{p}</p>

        <p>Train Name:{t}</p>

        <p>First Name:{fn}</p>

        <p>Last Name:{ln}</p>

        <p>train No.:{tn}</p>

        <p>email:{e}</p>
        <p>age:{age}</p>

        <p>Start from :{start_from}</p>
        <p>destination:{destination}</p>
        <p>coach no:{coach_no}</p>
        <p>seat no:{seat_no}</p>
        <Link className="btn btn-warning" to={`/payment`}>
          Payment
        </Link>


        <Button
              onClick={(o) => saveBooking(o)}
              //variant=" #2c7f86;"
              type="submit"
              id="submit"
              style={{
                marginTop: "1rem",
                fontFamily: "serif",
                paddingTop: "5px",
                width: "19rem",
                alignItems: "center",
                marginLeft: "250px",
                fontSize: "23px",
              }}
            >
              Confirm Booking
            </Button>
            <Modal show={show} onHide={handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Booking successfull!</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                Click "yes" to return to profile
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="primary" >
                  <Link to="/profile" style={{color:"white"}}>YES </Link>
                </Button>
              </Modal.Footer>
            </Modal>
      </div>
    </div>
  );
};

export default Fullticket;
