import React from "react";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import getByTrainName from "../services/train.service";
import axios from 'axios';


const TrainDetails = () => {
  const [search, setSearch] = useState([]);
  // const[record,setRecord]=useState([]);
  const [train, setTrain] = useState("");
let p = localStorage.getItem("name");
 
  // Search Item by Name
  const searchRecords = () => {
    getByTrainName
    .getByTrainName(p)
    .then((response) => {
      setSearch(response.data);
    });
  };

  return (
    <div className="tam">
      <h3
        style={{
          color: "black",
          fontWeight: "bolder",
          fontSize: "40px",
          textAlign: "center",
        }}
      >
        List of Trains
      </h3>

      <div className="tlist">
        <div>
          <div class="form-outline">
            <input
              type="text"
              id="form1"
              onChange={(e) => setTrain(e.target.value)}
              class="form-control"
              placeholder="search record"
              style={{ backgroundColor: "#ececec" }}
            />
          </div>
          <button type="button" onClick={searchRecords} class="btn btn-success">
            Search Train
          </button>
        </div>
        <table className="table table-bordered table-dark table-striped">
          <thead className="thead-light">
            <tr>
              <th> Train Name</th>
              <th> Start From</th>
              <th> Departure Time</th>
              <th> To Destination</th>
              <th> Arrival Time</th>
              <th> Ticket Price</th>
            </tr>
          </thead>
          <tbody>
          {
            <tr key={search.id}>
              <td>{search.train_name}</td>
              <td>{search.start_from}</td>
              <td>{search.departure_time}</td>
              <td>{search.to_destination}</td>
              <td>{search.arrival_time}</td>
              <td>{search.price}</td>
            </tr>
          }
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TrainDetails;
