import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import bootstrap from "bootstrap/dist/css/bootstrap.min.css";
import userservice from "../services/user.service";

const Bookingbyemail= () => {
    const [booking, setBooking] = useState([]);
    const [emailId, setEmailId] = useState([]);

  const init = () => {
    if(emailId){
        userservice

      .getBookingByemail(emailId)
      .then((response) => {
        console.log("Printing all booking ", response.data);
        setBooking(response.data);
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
    }
    else{
        window.alert("EmailId doesnt exist!")
    }
  };

  useEffect(() => {
    init();
  }, []);

  return (

    <div className="tam" >
         <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "20px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Enter your Email
              </label>
              <input
                type="text"
                placeholder="Enter Your Email"
                class="form-control"
                value={emailId}
                onChange={(e) => setEmailId(e.target.value)}
              />
            </div>
      <h3 style={{ color: "black", fontWeight: "bolder", fontSize: "30px", textAlign:"center" }}>
        List of Booking
      </h3>
      <hr />
      <div className="tlist" >
        <table className="table table-bordered table-dark table-striped" >
          <thead className="thead-light">
            <tr>
              <th> Train Name</th>
              <th> Train No</th>
              <th> FirstName</th>
              <th> LastName</th>
              <th> Age</th>
              <th> Start from</th>
              <th> Destination</th>
              <th> Actions </th>
            </tr>
          </thead>
          <tbody>
            {booking.map((bookings) => (
              <tr key={bookings.id}>
                 <td>{bookings.train_name}</td>
                <td>{bookings.train_no}</td>
                <td>{bookings.fname}</td>
                <td>{bookings.lname}</td>
                <td>{bookings.age}</td>
                <td>{bookings.start_from}</td>
                <td>{bookings.destination}</td>
                <td>  
                  <Link
                    className="btn btn-warning"
                    to={`/booking/edit/${bookings.id}`}
                  >
                    Update
                  </Link>
                  </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
     
    </div>
  );
}

export default Bookingbyemail;
