import React from "react";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import trainservice from "../services/train.service";
let p = localStorage.getItem("train_name");

const TrainDetails = () => {
  const [search, setSearch] = useState([]);
  // const[record,setRecord]=useState([]);
  const [train, setTrain] = useState("");

  // const navigate = useNavigate();

  // const init = () => {
  //   trainservice
  //     .getByTrainName(p)
  //     .then((response) => {
  //       console.log("Printing Train data", response.data);
  //       setTrain(response.data);
  //     })
  //     .catch((error) => {
  //       console.log("Something went wrong", error);
  //     });
  // };

  const loadRecords = async () => {
    // axios.get("http://localhost:3000/products")
    trainservice.getAll.then((response) => {
      setSearch(response.data);
    });
  };
  // useEffect(() => {
  //   init();
  // }, []);
  useEffect(() => {
    loadRecords();
  }, []);

  // Search Item by Name
  const searchRecords = () => {
    trainservice.getByTrainName.then((response) => {
      setSearch(response.data);
    });
  };

  return (
    <div className="showcase">
      <h3
        style={{
          color: "black",
          fontWeight: "bolder",
          fontSize: "40px",
          textAlign: "center",
        }}
      >
        List of Trains
      </h3>
      
      <div className="tlist">
        <button type="button" > </button>
        <table className="table table-bordered table-dark table-striped">
          <thead className="thead-light">
            <tr>
              <th> Train Name</th>
              <th> Start From</th>
              <th> Departure Time</th>
              <th> To Destination</th>
              <th> Arrival Time</th>
              <th> Ticket Price</th>
            </tr>
          </thead>
          <tbody>
            <tr key={train.train_name}>
              <td>{train.train_name}</td>
              <td>{train.start_from}</td>
              <td>{train.departure_time}</td>
              <td>{train.to_destination}</td>
              <td>{train.arrival_time}</td>
              <td>{train.price}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TrainDetails;
