import React from "react";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import AuthService from "../services/auth-service";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";
import bookingservice from "../services/booking.service";
let p = localStorage.getItem("username");
const Addbooking = () => {


  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);



  const [user, setFaculty] = useState([]);
  const [train_no, setTrainNo] = useState("");
  const [train_name, setTrainName] = useState("");

  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [age, setAge] = useState("");
  const [emailId, setEmailId] = useState("");
  const [start_from, setStartFrom] = useState("");
  const [destination, setDestination] = useState("");
  const [coach_no, setCoach] = useState("");
  const [seat_no, setSeat] = useState("");

  const navigate = useNavigate();
  const { id } = useParams();

  
  const init = () => {
    AuthService.getUser(p)
      .then((response) => {
        console.log("Printing faculty data", response.data);
        setFaculty(response.data);
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };
  useEffect(() => {
    init();
  }, []);
  const saveBooking = (e) => {
    e.preventDefault();

    const booking = {
      train_no,
      train_name,
      fname,
      lname,
      age,
      start_from,
      destination,
      emailId,
      coach_no,
      seat_no,
      id,
    };

    if (id) {
      //update
      bookingservice
        .update(booking)
        .then((response) => {
          console.log("booking data updated successfully", response.data);
          localStorage.setItem("email", booking.emailId);
          localStorage.setItem("train_name", booking.train_name);
          localStorage.setItem("train_no", booking.train_no);
          localStorage.setItem("fname", booking.fname);
          localStorage.setItem("lname", booking.lname);
          localStorage.setItem("age", booking.age);
          localStorage.setItem("bookingId", booking.id);
          localStorage.setItem("start_from", booking.start_from);
          localStorage.setItem("destination", booking.destination);
          localStorage.setItem("coach_no", booking.coach_no);
          localStorage.setItem("seat_no", booking.seat_no);
          window.location.href = "/fullticket";
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      bookingservice
        .create(booking)
        .then((response) => {
          console.log("booking added successfully", response.data);
          localStorage.setItem("email", booking.emailId);
          localStorage.setItem("train_name", booking.train_name);
          localStorage.setItem("train_no", booking.train_no);
          localStorage.setItem("fname", booking.fname);
          localStorage.setItem("lname", booking.lname);
          localStorage.setItem("age", booking.age);
          localStorage.setItem("bookingId", booking.id);
          localStorage.setItem("start_from", booking.start_from);
          localStorage.setItem("coach_no", booking.coach_no);
          localStorage.setItem("seat_no", booking.seat_no);

          window.location.href = "/fullticket";

          localStorage.setItem("email", booking.emailId);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (id) {
      bookingservice
        .get(id)
        .then((bookings) => {
          setTrainNo(bookings.data.train_no);
          setTrainName(bookings.data.train_name);
          setFname(bookings.data.fname);
          setLname(bookings.data.lname);
          setAge(bookings.data.age);
          setStartFrom(bookings.data.start_from);
          setDestination(bookings.data.destination);
          setEmailId(bookings.data.emailId);
          setCoach(bookings.data.coach_no);
          setSeat(bookings.data.seat_no);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <>
      <section >
        <div class="container d-flex justify-content-center" >
          <form className="form-control"style={{
              width: "55rem",
              height: "31rem",
              marginTop:"10px",
              marginRight: "15rem",
              backgroundColor: "powderblue",
              boxShadow: "1px 2px 9px #F4AAB9",
            }} >
             {/* <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
               setTrainNo
              </label>
              <input
                type="text"
                placeholder="TrainNo"
                class="form-control"
                value={train_no}
                onChange={(e) => setTrainNo(e.target.value)}
              />
            </div> */}
            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Enter TrainName
              </label>
              <input
                type="text"
                placeholder="TrainName"
                class="form-control"
                value={train_name}
                onChange={(e) => setTrainName(e.target.value)}
              />
            </div>
            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Enter your First Name
              </label>
              <input
                type="text"
                placeholder="FirstName"
                class="form-control"
                value={fname}
                onChange={(e) => setFname(e.target.value)}
              />
            </div>
            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Enter your Last Name
              </label>
              <input
                type="text"
                placeholder="LastName"
                class="form-control"
                value={lname}
                onChange={(e) => setLname(e.target.value)}
              />
            </div>
            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Enter your Age
              </label>
              <input
                type="number"
                placeholder="Age"
                class="form-control"
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
            </div>
            {/* <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Start From
              </label>
              <input
                type="text"
                placeholder="start_from"
                class="form-control"
                value={start_from}
                onChange={(e) => setStartFrom(e.target.value)}
              />
            </div> */}
            
            {/* <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Destination
              </label>
               <input
                type="text"
                placeholder="destination"
                class="form-control"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
              />
            </div>  */}

            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Set Coach no: 
              </label>
              <input
                type="text"
                placeholder="coach_no"
                class="form-control"
                value={coach_no}
                onChange={(e) => setCoach(e.target.value)}
              />
            </div>

            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{
                  fontSize: "15px",
                  color: "black",
                  marginLeft: "10px",
                  fontWeight: "bolder",
                }}
              >
                Seat no: 
              </label>
              <input
                type="text"
                placeholder="coach_no"
                class="form-control"
                value={seat_no}
                onChange={(e) => setSeat(e.target.value)}
              />
            </div>

            <Button
              onClick={(o) => saveBooking(o)}
              //variant=" #2c7f86;"
              type="submit"
              id="submit"
              style={{
                marginTop: "1rem",
                fontFamily: "serif",
                paddingTop: "5px",
                width: "19rem",
                alignItems: "center",
                marginLeft: "250px",
                fontSize: "23px",
              }}
            >
              Confirm Booking
            </Button>
            <Modal show={show} onHide={handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Booking successfull!</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                Click "yes" to return to profile
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="primary" onClick={(o) => saveBooking(o)}>
                  <Link to="/profile" style={{color:"white"}}>YES </Link>
                </Button>
              </Modal.Footer>
            </Modal>
          </form>
        </div>
      </section>
    </>
  );
};

export default Addbooking;
