import React from "react";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import bookingservice from "../services/booking.service";

const AddTicket = () => {
  const [coach_no, setCoach] = useState("");
  const [seat_no, setSeat] = useState("");
  const [emailId, setEmailId] = useState("");

  const navigate = useNavigate();
  const { id } = useParams();

  const saveTicket = (e) => {
    e.preventDefault();

    const ticket = { coach_no,seat_no,emailId, id };

    if (id) {
      //update
      bookingservice
        .update(ticket)
        .then((response) => {
          console.log("ticket data updated successfully", response.data);
          navigate("/Booking");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      bookingservice
        .createTicket(ticket)
        .then((response) => {
          console.log("ticket added successfully", response.data);
          navigate("/AddTicket");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (id) {
      bookingservice
        .get(id)
        .then((tickets) => {
          setCoach(tickets.data.coach_no);
          setTrainName(tickets.data.train);
          setFname(tickets.data.fname);
        
        
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <>
      <section className="showcase login">
        <div className="showcase-overlay">
          <form className="form-control">
            <input
              type="number"
              placeholder="TrainNo"
              value={train_no}
              onChange={(e) => setTrainNo(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter TrainName"
              value={train_name}
              onChange={(e) => setTrainName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter Firstname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
            />
             <input
              type="text"
              placeholder="Enter Lastname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
            />
            <input
              type="number"
              placeholder="Age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
            />
            <input
              type="text"
              placeholder="Start From"
              value={start_from}
              onChange={(e) => setStartFrom(e.target.value)}
            />
            <input
              type="text"
              placeholder="Destination"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
            />

            
            <Button
              onClick={(o) => saveBooking(o)}
              //variant=" #2c7f86;"
              type="submit"
              id="submit"
            >
              Confirm Booking
            </Button>
          </form>
        </div>
      </section>
    </>
  );
};

export default Addbooking;
