import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Button from "react-bootstrap/Button";

import bootstrap from "bootstrap/dist/css/bootstrap.min.css";
import userservice from "../services/user.service";

const Bookingbyemail = () => {
  const [booking, setBooking] = useState([]);
  const [emailId, setEmailId] = useState([]);

  const init = (emailId) => {
    if (emailId) {
      userservice

        .getBookingByemail(emailId)
        .then((response) => {
          console.log("Printing all booking ", response.data);
          setBooking(response.data);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      window.alert("EmailId doesnt exist!");
    }
  };

  useEffect(() => {
    init();
  }, []);


    useEffect(()=>{
    if(emailId){
      userservice.getBookingByemail(emailId)  .then((bookings) => {
        setTrainNo(bookings.data.train_no);
        setTrainName(bookings.data.train_name);
        setFname(bookings.data.fname);
        setLname(bookings.data.lname);
        setAge(bookings.data.age);
        setStartFrom(bookings.data.start_from);
        setDestination(bookings.data.destination);
        setEmailId(bookings.data.emailId);
        setCoach(bookings.data.coach_no);
        setSeat(bookings.data.seat_no);
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  }
}, []);
  return (
    <div className="tam">
      <div class="form-group pt-2 pl-1">
        <label
          for="exampleInputName"
          style={{
            fontSize: "20px",
            color: "black",
            marginLeft: "10px",
            fontWeight: "bolder",
          }}
        >
          Enter your Email
        </label>
        <input
          type="text"
          placeholder="Enter Your Email"
          class="form-control"
          value={emailId}
          onChange={(e) => setEmailId(e.target.value)}
        />
      </div>
      <Button
        onClick={() => init(booking.emailId)}
        //variant=" #2c7f86;"
        type="submit"
        id="submit"
      >
        Find Booking
      </Button>
      <h3
        style={{
          color: "black",
          fontWeight: "bolder",
          fontSize: "30px",
          textAlign: "center",
        }}
      >
        List of Booking
      </h3>
      <hr />
      <div className="tlist">
        <table className="table table-bordered table-dark table-striped">
          <thead className="thead-light">
            <tr>
              <th> Train Name</th>
              <th> Train No</th>
              <th> FirstName</th>
              <th> LastName</th>
              <th> Age</th>
              <th> Start from</th>
              <th> Destination</th>
              <th> Actions </th>
            </tr>
          </thead>
          <tbody>
            {booking.map((bookings) => (
              <tr key={bookings.id}>
                <td>{bookings.train_name}</td>
                <td>{bookings.train_no}</td>
                <td>{bookings.fname}</td>
                <td>{bookings.lname}</td>
                <td>{bookings.age}</td>
                <td>{bookings.start_from}</td>
                <td>{bookings.destination}</td>
                <td></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Bookingbyemail;
