import React, { useEffect, useState } from 'react'
import { Link,useNavigate } from 'react-router-dom'
import Button from "react-bootstrap/Button";
import AuthService from "../services/auth-service";
import Form from "react-bootstrap/Form";
import logo1 from '../assets/logo1.jpg'
import trainService from '../services/train.service';

const Header = () => {

  const navigate = useNavigate();
  const logOut = () => {
    AuthService.logout();
    navigate("/");
  };
  const [train,setTrain]=useState([]);
  const [train_name,setTrainName]=useState([]);

  const search=(e)=>{
    e.preventDefault();

    const val = {
      train_name

    };

    trainService
      .getByTrainName(val)
      .then((response) => {
        console.log("Train added successfully", response.data);

        localStorage.setItem("train_name", val.train_name);
        localStorage.setItem("train", response.data);

         window.location.href="/TrainDetails"
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });

  }
  // const getByTrainName=(id)=> {
  //   console.log(id);
  //   trainService.getByTrainName(id).then((response)=> {
  //     console.log("All Data",response.data);
  //     setTrain(response.data);
  //   }).catch((error)=> {
  //     console.log("Something went wrong",error);
  //   });
  // }

  // useEffect(()=> {
  //   getByName();
  // },[]);

  return (
    <header className='header'>

      <div>
      <img
          src={logo1}
          alt=""
          style={{
             height: "26px",
             width: "66px",
             paddingRight: "20px",


          }}
        />
      </div>
      {/* <div>
        <Link className='links' to='/'>
          Home
        </Link>
      </div> */}

      <nav className='navbar' style={{height:"40px"}}>
        <ul>
        <Link className='links' to='/'>
          Home
        </Link>
          <Link className='links' to='/login'>
           User Login
          </Link>
          <Link className='links' to='/Adminlogin'>
           Admin Login
          </Link>
          <Link className='links' to='/signup'>
            Register
          </Link>
          <Link className='links' to='/Contact'>
            Contact Us
          </Link>
          <Link className='btn btn-warning' to='/Train'>
           Train
          </Link>
          <button
            style={{ marginLeft: "88rem", marginTop:"10px" }}
            className="btn btn-danger "
            onClick={() => {
              logOut();
            }}
          >
            LOGOUT
          </button>
        </ul>
      </nav>
      <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              value={train_name}
              onChange={(e) => setTrainName(e.target.value)}
              className="me-2"
              aria-label="Search"
              style={{marginLeft: "450px",height:"30px",width:"290px"}}
            />
            {/* <Link className="btn btn-sucess" to="/TrainDetails" id="search" variant="secondary" style={{marginRight: "30px", color: "white"}} 
            onClick={(e) => {
              search(e);
            }}>
              Search
            </Link> */}
            <button
                    class="btn btn-warning homecontactbtn1"
                    onClick={(e) => {
                      search(e);
                    }}
                  >
                    Search
                  </button>
      </Form>
    </header>
  )
}

export default Header
