import React from "react";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import userservice from "../services/user.service";

const SignUp = () => {
  const [username, setName] = useState("");
  const [emailId, setEmail] = useState("");

  const [password, setPass] = useState("");
  const [age, setAge] = useState("");

  const [gender, setGender] = useState("");
  const [ph_no, setPhone] = useState("");
  const [roles, setRoles] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const saveUser = (e) => {
    e.preventDefault();

    const user = { username, emailId, password, age, gender, ph_no, roles, id };

    if (id) {
      //update
      userservice
        .update(user)
        .then((response) => {
          console.log("user data updated successfully", response.data);
          navigate("/user");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      userservice
        .create(user)
        .then((response) => {
          console.log("user added successfully", response.data);
          navigate("/registration");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (id) {
      userservice
        .get(id)
        .then((user) => {
          setName(user.data.username);
          setEmail(user.data.emailId);
          setPass(user.data.password);
          setAge(user.data.age);
          setGender(user.data.gender);
          setPhone(user.data.ph_no);
          setRoles(user.data.roles);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <section>
      <div>
        <div class="container d-flex justify-content-center">
          <form
            className="form-control"
            style={{
              width: "33rem",
              height: "12rem",
              marginTop: "9rem",
              marginRight: "15rem",
              backgroundColor: "black",
            }}
          >
            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{ fontSize: "20px", color: "white" }}
              >
                Enter your UserName
              </label>
              <input
                type="text"
                placeholder="UserName"
                value={username}
                onChange={(e) => setName(e.target.value)}
              />
            </div>

            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{ fontSize: "20px", color: "white" }}
              >
                Enter your EmailId
              </label>
              <input
                type="email"
                placeholder="emailId"
                value={emailId}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div class="form-group pt-2 pl-1">
              <label
                for="exampleInputName"
                style={{ fontSize: "20px", color: "white" }}
              >
                Enter your Password
              </label>
              <input
                type="text"
                placeholder="Enter y"
                value={emailId}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <input
              type="number"
              placeholder="Age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
            />
            <input
              type="text"
              placeholder="Gender"
              value={gender}
              onChange={(e) => setGender(e.target.value)}
            />
            <input
              type="number"
              placeholder="Mobile No."
              value={ph_no}
              onChange={(e) => setPhone(e.target.value)}
            />

            <input
              type="text"
              placeholder="user/admin"
              value={roles}
              onChange={(e) => setRoles(e.target.value)}
            />
            <Button
              onClick={(o) => saveUser(o)}
              //variant=" #2c7f86;"
              type="submit"
              id="submit"
            >
              Create Account
            </Button>
          </form>
        </div>
      </div>
    </section>
  );
};

export default SignUp;
